from math import floor, ceil

MORE = 1
EQUALS = 0
LESS = -1

def bruteforce(oracle, charset, length=-1):
    assert len(charset) > 0
    result = ""
    while len(result) < length or length==-1:
        low = 0
        high = len(charset)-1
        mid = floor((high+low) / 2)
        char = None
        while low <= high:
            c = charset[mid]
            if low == high and low != len(charset)-1:
                char = [c]
                break
            oracleresult = oracle(result+c, charset)
            if oracleresult == MORE:
                low = mid+1
            elif oracleresult == LESS:
                high = mid-1
            else:
                char = [c]
                break
            mid = floor((high+low) / 2)
        if char == None:
            return result
        else:
            result += char[0]

























